import { useQuery } from '@tanstack/react-query';
import React, { useState } from 'react';
import AllbuyerTable from '../../../Components/AllbuyerTable';
import ConfirmationModal from '../../../Components/ConfirmationModal';

const Allbuyer = () => {
    const { data: buyers = [] } = useQuery({
        queryKey: ['buyers'],
        queryFn: async () => {
            const res = await fetch(`http://localhost:5001/allbuyers`, {
                headers: {
                    authorization: `Bearer ${localStorage.getItem('arkDeals')}`
                }
            })
            const data = await res.json();
            return data
        }
    })

    const [deletingBuyer, setDeletingBuyer] = useState(null);

    const confirmationModal = user => {
        setDeletingBuyer(user)
    }

    return (
        <div>
      <div className="bg-base-100">
        <h2 className="text-center text-xl">All Buyer</h2>
        <div className="overflow-x-auto w-full">
          <table className="table w-full ">
            <thead>
              <tr>
                <th></th>
                <th>Seller Name</th>
                <th>Email</th>

                <th></th>
              </tr>
            </thead>
            <tbody>
            {buyers.map((buyer,i) =><AllbuyerTable key={buyer._id} buyer={buyer} confirmationModal={confirmationModal} i={i} ></AllbuyerTable>)}
            </tbody>
          </table>
        </div>
      </div>
      {deletingBuyer && (
        <ConfirmationModal
          title={`Your are deleting ${deletingBuyer.name}`}
                    message={`Everything associated with this ${deletingBuyer.name} including products,orders,advertisements will be deleted from database. 
          Since your are using firebase authentication, you must delete ${deletingBuyer.email} from there manually!`}
          successAction={handleDelete}
          successButtonName="Delete"
          modalData={deletingSeller}
          closeModal={closeModal}
        ></ConfirmationModal>
      )}
    </div>
    );
};

export default Allbuyer;
