import { useQuery } from '@tanstack/react-query';
import React, { useContext } from 'react';
import { useLoaderData } from 'react-router-dom';
import { AuthContext } from '../../Context/AuthProvider';
import useAdmin from '../../hooks/useAdmin';
import useSeller from '../../hooks/useSeller';

const Product = () => {
    const product = useLoaderData();
    const { user } = useContext(AuthContext);
    const [isSeller] = useSeller(user?.email);
    const [isAdmin] = useAdmin(user?.email);
    const {
        _id,
        sellerName,
        sellerLocation,
        categoryName,
        productName,
        brand,
        resalePrice,
        originalPrice,
        yearOfPurchase,
        condition,
        productImage,
        productDescription,
          timeOfPost,
      } = product;
    return (
        <div className="card w-full bg-base-300 shadow-xl">
  <figure className="px-10 pt-10">
    <img src={productImage} alt="Laptop" className="rounded-xl" />
  </figure>
  <div className="card-body">
        <h2 className="card-title">
          {productName}
          <div className="badge badge-secondary">${resalePrice}</div>
        </h2>
        <div className="font-custom2">
          <p>Seller: {sellerName}</p>
          <p>Location: {sellerLocation}</p>
          <p>Original Price: ${originalPrice}</p>
                    <p>Year of Purchase: {yearOfPurchase}</p>
                    <p className="text-xl font-bold"></p>
        </div>
        <div className="card-actions justify-end">
          <div className="badge badge-outline">{brand}</div>
          <div className="badge badge-outline">{categoryName}</div>
          <div className="badge badge-outline">{condition}</div>

          <div className="badge badge-outline">{timeOfPost}</div>
                </div>

        <div className="card-actions justify-center mt-2">
          <button disabled={isSeller || isAdmin} className="btn btn-outline btn-primary">Book Now</button>
        </div>
      </div>
</div>
    );
};

export default Product;
