import { useQuery } from '@tanstack/react-query';
import React, { useContext, useState } from 'react';
import { AuthContext } from '../../../Context/AuthProvider';

const MyBuyers = () => {
    const { user } = useContext(AuthContext);
    const [meetingOrder, setMeetingOrder] = useState(null)
    const { data: orders = [], refetch } = useQuery({
        queryKey: ['orders'],
        queryFn: async () => {
            const res = await fetch(`http://localhost:5001/mybuyers?email=${user?.email}`, {
                headers: {
                    authorization: `bearer ${localStorage.getItem('arkDeals')}`
                }
            })
            const data = await res.json();
            return data
        }
    })
    console.log(orders)
    return (
        <div>
      <div className="bg-base-100">
        <h2 className="text-center text-xl">My Orders</h2>
        <div className="overflow-x-auto w-full">
          <table className="table w-full ">
            <thead>
              <tr>
                <th></th>
                <th>Laptop</th>
                <th>Seller Details</th>
                <th>Price</th>
                <th>Action</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {orders.map((order, i) => (
                <MyOrdersTable
                  key={order.pId}
                  order={order}
                  confirmationModal={confirmationModal}
                  i={i}
                ></MyOrdersTable>
              ))}
            </tbody>
          </table>
        </div>
      </div>
      {payOrder && (
        <ConfirmationModal
          title={`Payment for ${payOrder.product.productName}`}
          message={`Your are going to pay $ ${payOrder.product.resalePrice}`}
          successAction={handlePay}
          successButtonName="Pay"
          modalData={payOrder}
          closeModal={closeModal}
        ></ConfirmationModal>
      )}
    </div>
    );
};

export default MyBuyers;
