import { useQuery } from '@tanstack/react-query';
import React from 'react';
import AllsellerTable from '../../../Components/AllsellerTable';

const Allselller = () => {
    const { data: sellers = [], refetch } = useQuery({
        queryKey: ['sellers'],
        queryFn: async () => {
            const res = await fetch(`http://localhost:5001/allsellers`, {
                headers: {
                    authorization: `Bearer ${localStorage.getItem('arkDeals')}`
                }
            })
            const data = await res.json()
            return data;
        }
    })
    console.log(sellers)
    return (
        <div>
      <div className="bg-base-100">
        <h2 className="text-center text-xl">All Seller</h2>
        <div className="overflow-x-auto w-full">
          <table className="table w-full ">
            <thead>
              <tr>
                <th></th>
                <th>Seller Name</th>
                <th>Email</th>
                <th>Status</th>
                <th></th>
              </tr>
            </thead>
            <tbody>
            {sellers.map((seller,i) =><AllsellerTable key={seller._id} seller={seller} confirmationModal></AllsellerTable>)}
            </tbody>
          </table>
        </div>
      </div>
      {/* {payOrder && (
        <ConfirmationModal
          title={`Payment for ${payOrder.product.productName}`}
          message={`Your are going to pay $ ${payOrder.product.resalePrice}`}
          successAction={handlePay}
          successButtonName="Pay"
          modalData={payOrder}
          closeModal={closeModal}
        ></ConfirmationModal>
      )} */}
    </div>
    );
};

export default Allselller;
