import { useQuery } from "@tanstack/react-query";
import React, { useContext, useState } from "react";
import { useNavigate } from "react-router-dom";
import { toast } from "react-toastify";
import ConfirmationModal from "../../../Components/ConfirmationModal";
import MyOrdersTable from "../../../Components/MyOrdersTable";
import { AuthContext } from "../../../Context/AuthProvider";

const MyOrders = () => {
  const { user } = useContext(AuthContext);
  const navigate = useNavigate();
  const [payOrder, setPayOrder] = useState(null);
  const { data: orders = [], refetch } = useQuery({
    queryKey: ["orders"],
    queryFn: async () => {
      const res = await fetch(
        `http://localhost:5001/myorders?email=${user?.email}`,
        {
          headers: {
            authorization: `Bearer ${localStorage.getItem("arkDeals")}`,
          },
        }
      );
      const data = await res.json();
      return data;
    },
  });

  const confirmationModal = (order) => {
    setPayOrder(order);
  };

  const handlePay = (order) => {
    console.log(order);
    fetch(`http://localhost:5001/orders-paid/${order._id}`, {
       method: 'PATCH'
    })
      .then(res => res.json())
      .then(data => {
        console.log("order status",data)
        fetch(`http://localhost:5001/orders-paid-dup/${order?.pId}`, {
          method: 'DELETE'
        })
          .then(res => res.json())
          .then(data => {
            console.log(data)
            if (data.deletedCount >= 1) {
              fetch(`http://localhost:5001/products-paid/${order.pId}`, {
                method: 'PATCH'
                })
                  .then(res => res.json())
                  .then(data => {
                    console.log("product status",data);
                    if (data.modifiedCount>=1) {
                      fetch(`http://localhost:5001/delete-advertisement/${order.pId}`, {
                        method: 'DELETE',

                      })
                        .then(res => res.json())
                        .then(data => {
                          console.log("delete",data)
                          if (data.acknowledged) {
                            refetch();
                            toast.success("Payment Succesfull!")
                          }
                      })
                  }
                })
            }
        })

    })
  };

  const handlePaymentPage = (order) => {
    navigate(`/`)
  }

  const closeModal = () => {
    setPayOrder(null);
  };

  return (
    <div>
      <div className="bg-base-100">
        <h2 className="text-center text-xl">My Orders</h2>
        <div className="overflow-x-auto w-full">
          <table className="table w-full ">
            <thead>
              <tr>
                <th></th>
                <th>Laptop</th>
                <th>Seller Details</th>
                <th>Price</th>
                <th>Action</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {orders.map((order, i) => (
                <MyOrdersTable
                  key={order.pId}
                  order={order}
                  confirmationModal={confirmationModal}
                  i={i}
                ></MyOrdersTable>
              ))}
            </tbody>
          </table>
        </div>
      </div>
      {payOrder && (
        <ConfirmationModal
          title={`Payment for ${payOrder.product.productName}`}
          message={`Your are going to pay $ ${payOrder.product.resalePrice}`}
          successAction={handlePaymentPage}
          successButtonName="Go to Payment Page"
          modalData={payOrder}
          closeModal={closeModal}
        ></ConfirmationModal>
      )}
    </div>
  );
};

export default MyOrders;
