import { useQuery } from '@tanstack/react-query';
import React from 'react';

const Reported = () => {
    const { data: items = [], reftech } = useQuery({
        queryKey: ['items'],
        queryFn: async () => {
            const res = await fetch(`http://localhost:5001/reported-item`, {
                headers: {
                    authorization: `bearer ${localStorage.getItem('arkDeals')}`
                }
            })
            const data = await res.json();
            return data
        }
    })
    console.log(items)
    return (
        <div className="bg-base-100">
        <h2 className="text-center text-xl">Reported Products</h2>
        <div className="overflow-x-auto w-full">
          <table className="table w-full ">
            <thead>
              <tr>
                <th></th>
                <th>Laptop</th>
                <th>Seller</th>
                <th>Reporter</th>
                <th>Message</th>
                <th></th>
                <th></th>
              </tr>
            </thead>
            <tbody>
                        {
                            items.map((item,i) =><Report key={product._id} product={product} i={i} handleAdvertise={handleAdvertise} handleRemoveAdvertise={handleRemoveAdvertise} confirmationModal={confirmationModal}></Report>)
             }
            </tbody>
          </table>
          {/* {deletingProduct && (
          <ConfirmationModal
            title={`Are your sure to delete ${deletingProduct.productName}`}
            message={`This operation can not be undone`}
            successAction={handleDelete}
            successButtonName="Confirm Delete"
            modalData={deletingProduct}
            closeModal={closeModal}
          ></ConfirmationModal>
        )} */}
        </div>
      </div>
    );
};

export default Reported;
