import { CardElement, useElements, useStripe } from '@stripe/react-stripe-js';
import React, { useEffect, useState } from 'react';

const CheckoutForm = ({ order }) => {
    const { product, _id, pId } = order;
    const {resalePrice} = product;
    const stripe = useStripe()
    const elements = useElements();
    const [cardError, setCardError] = useState('');
    const [clientSecret, setClientSecret] = useState("");

    // useEffect(() => {
    //     // Create PaymentIntent as soon as the page loads
    //     fetch(`http://localhost:5001/create-payment-intent`, {
    //       method: "POST",
    //         headers: {
    //             "Content-Type": "application/json",
    //             authorization: `bearer ${localStorage.getItem('arkDeals')}`
    //         },
    //       body: JSON.stringify({ resalePrice }),
    //     })
    //       .then((res) => res.json())
    //       .then((data) => setClientSecret(data.clientSecret));
    //   }, [resalePrice]);



    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!stripe || !elements) {
            return;
        }

        const card = elements.getElement(CardElement);

        if (card === null) {
            return;
        }

        const {error,paymentMethod } = await stripe.createPaymentMethod({
            type: 'card',
            card
        })

        if (error) {
            setCardError(error.message)

        }
        else {
            setCardError('')
        }

    }
    return (
        <>
        <form onSubmit={handleSubmit}>
            <CardElement
                className='w-1/2 border'
        options={{
          style: {
            base: {
              fontSize: '16px',
              color: '#424770',
              '::placeholder': {
                color: '#aab7c4',
              },
            },
            invalid: {
              color: '#9e2146',
            },
          },
        }}
      />
      <button type="submit" className='btn btn-sm w-1/2' disabled={!stripe}>
        Pay
      </button>
            </form>
            <p className="text-red-500">{cardError}</p>
        </>
    );
};

export default CheckoutForm;
