    queryKey: ["orders"],
    queryFn: async () => {
      const res = await fetch(
        `http://localhost:5001/orders?email=${user?.email}`,
        {
          headers: {
            authorization: `Bearer ${localStorage.getItem("arkDeals")}`,
          },
        }
      );
      const data = await res.json();
      return data;
    },
  });

