                      <div className="flex items-center space-x-3">
                        <div className="avatar">
                          <div className="mask mask-squircle w-12 h-12">
                            <img
                              src={order.product.productImage}
                              alt={order.product.productName}
                            />
                          </div>
                        </div>
                        <div>
                              <div className="font-bold">
                                  {order.product.productName}
                          </div>
                              <div className="text-sm opacity-50 font-custom2">
                                  {order.product.categoryName}
                          </div>
                        </div>
                      </div>
                    </td>
                    <td>
                     {order.product.sellerName}
                      <br />
                      <span className="badge badge-ghost badge-sm font-custom2">
                          {order.product.sellerEmail}
                      </span>
                      <br />
                      <span className="badge badge-ghost badge-sm font-custom2">
                      {
                              order.product.sellerPhone
