import { useQuery } from "@tanstack/react-query";
import React from "react";
import { Outlet, useNavigate } from "react-router-dom";

const BrowseLayout = () => {
  const navigate = useNavigate()

  const { data: categories = [] } = useQuery({
    queryKey:[ 'categories'],
    queryFn: async () => {
      const res = await fetch(`http://localhost:5001/categories`);
      const data = await res.json();
      return data;
    }
  }) 

    const handleCategory = (e) => {
        e.preventDefault();
        const form = e.target;
        const categoryName = form.category.value;
      const category = categories.find(c => c.categoryName === categoryName)



}

  return (
    <div className="flex flex-col justify-center items-center">
          <form onSubmit={handleCategory} className="">
          <div className="form-control">
        <div className="input-group">
          <select name='category' className="select select-bordered">
            <option disabled selected>
             All Categories
            </option>
              {
                categories.map(category => <option key={category._id}>
                  {category.categoryName}
                </option>)
           }

          </select>
        <input className="btn" type="submit" value="go" />
        </div>
      </div>
          </form>

              <Outlet></Outlet>

    </div>
  );
};

export default BrowseLayout;
